    const namesList = [
        {link: "https://google.com", color: "red", age:12, name: 'Svyryd', img: "data:image/jpeg;base64,"},
        {link: "https://google.com", color: "green", age:28, name: 'Opanas', img: "data:image/jpeg;base64,"},
        {link: "https://google.com", color: "blue", age:67, name: 'Nestor Ivanovych Makhno', img: "data:image/jpeg;base64,"},
        {link: "https://google.com", color: "orange", age:28, name: 'Bogdan', img: "data:image/jpeg;base64,"},
        {link: "https://google.com", color: "pink", age:23, name: 'Ostap', img: "data:image/jpeg;base64,"},
        {link: "https://google.com", color: "coral", age:54, name: 'Nazar', img: "data:image/jpeg;base64,"}
    ];

    const app = document.querySelector('#app');
    if (app) {
    
        let html = '';
    
        namesList.forEach((obj, i) => {
            if (i % 2) {
                html += `<li class="names-item" style="background: ${obj.color}">
                    <img src="${obj.img}"/>
                    name: ${obj.name}; 
                    ${obj.age > 40 ? 'too old' : `age: ${obj.age}`}
                    <a href="${obj.link}" target="__blank">google</a>
                </li>`
            }
        });
    
        html = `<ul class="names">${html}</ul>`;
    
        app.innerHTML = html;
    }
